import React from 'react';

const Skills = () => {
  const skillCategories = [
    {
      title: "Frontend",
      skills: [
        { name: "HTML5", icon: "devicon-html5-plain colored" },
        { name: "Bootstrap", icon: "devicon-bootstrap-plain colored" },
        { name: "Tailwind CSS", icon: "devicon-tailwindcss-plain colored" },
        { name: "React", icon: "devicon-react-original colored" },
        { name: "React Native", icon: "devicon-react-original colored" }
      ]
    },
    {
      title: "Backend",
      skills: [
        { name: "Django", icon: "devicon-django-plain colored" },
        { name: "Laravel", icon: "devicon-laravel-plain colored" },
        { name: "Spring Boot", icon: "devicon-spring-plain colored" }
      ]
    },
    {
      title: "Languages",
      skills: [
        { name: "Python", icon: "devicon-python-plain colored" },
        { name: "Java", icon: "devicon-java-plain colored" },
        { name: "PHP", icon: "devicon-php-plain colored" },
        { name: "JavaScript", icon: "devicon-javascript-plain colored" },
        { name: "C++", icon: "devicon-cplusplus-plain colored" }
      ]
    },
    {
      title: "Databases",
      skills: [
        { name: "PostgreSQL", icon: "devicon-postgresql-plain colored" },
        { name: "Oracle", icon: "devicon-oracle-plain colored" },
        { name: "MariaDB", icon: "devicon-mariadb-plain colored" },
        { name: "MySQL", icon: "devicon-mysql-plain colored" }
      ]
    },
    {
      title: "Tools & Systems",
      skills: [
        { name: "Git", icon: "devicon-git-plain colored" },
        { name: "GitHub", icon: "devicon-github-plain colored" },
        { name: "Linux", icon: "devicon-linux-plain colored" },
        { name: "Apache", icon: "devicon-apache-plain colored" }
      ]
    },
    {
      title: "Data & Analytics",
      skills: [
        { name: "Pandas", icon: "devicon-pandas-plain colored" }
      ]
    }
  ];

  return (
    <section id="skills" className="px-6 md:px-12 lg:px-24 py-16 md:py-24 bg-tech-blue text-white">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold mb-6">
            Technical <span className="gradient-text">Skills</span>
          </h2>
          <div className="w-24 h-1 bg-gradient-to-r from-tech-cyan to-tech-green mx-auto mb-8"></div>
          <p className="text-xl text-tech-light/80 max-w-3xl mx-auto">
            Expertise across the full technology stack, from frontend to infrastructure
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {skillCategories.map((category, catIndex) => (
            <div 
              key={catIndex}
              className="bg-tech-navy rounded-xl p-8 shadow-lg border border-tech-cyan/20"
            >
              <h3 className="text-2xl font-bold mb-8 text-tech-cyan text-center">
                {category.title}
              </h3>
              <div className="grid grid-cols-3 gap-6">
                {category.skills.map((skill, skillIndex) => (
                  <div 
                    key={skillIndex}
                    className="flex flex-col items-center group cursor-pointer"
                  >
                    <div className="w-16 h-16 flex items-center justify-center mb-3 group-hover:transform group-hover:scale-110 transition-transform duration-300">
                      <i className={`${skill.icon} text-5xl`}></i>
                    </div>
                    <span className="text-sm font-medium text-tech-light/90 group-hover:text-tech-cyan transition-colors duration-300 text-center">
                      {skill.name}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        <div className="mt-16 text-center">
          <div className="inline-block bg-gradient-to-r from-tech-cyan/20 to-tech-green/20 rounded-full px-8 py-4">
            <p className="text-lg font-semibold text-tech-light">
              Continuously expanding my skills with emerging technologies and best practices
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
