import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:8000/api/contact/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setSubmitMessage(data.message || 'Thank you! Your message has been sent successfully.');
        setFormData({ name: '', email: '', message: '' });
      } else {
        setSubmitMessage(data.message || 'Something went wrong. Please try again.');
      }
    } catch (error) {
      setSubmitMessage('Failed to send message. Please check your connection or try the WhatsApp option.');
      console.error('Error:', error);
    } finally {
      setIsSubmitting(false);
      setTimeout(() => setSubmitMessage(''), 5000);
    }
  };

  const whatsappLink = `https://wa.me/255750792039?text=Hi%20Kelvin,%20I%20saw%20your%20portfolio%20and%20would%20like%20to%20discuss%20a%20project.`;

  return (
    <section id="contact" className="px-6 md:px-12 lg:px-24 py-16 md:py-24 bg-gradient-to-br from-tech-blue to-tech-navy text-white">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold mb-6">
            Let's Build <span className="gradient-text">Something Great</span>
          </h2>
          <div className="w-24 h-1 bg-gradient-to-r from-tech-cyan to-tech-green mx-auto mb-8"></div>
          <p className="text-xl text-tech-light/80 max-w-3xl mx-auto">
            Have a project in mind? Let's discuss how we can bring your vision to life with secure, scalable architecture.
          </p>
        </div>

        <div className="grid lg:grid-cols-2 gap-12">
          {/* Contact Form */}
          <div className="bg-tech-navy/50 backdrop-blur-sm rounded-2xl p-8 shadow-2xl border border-tech-cyan/20">
            <h3 className="text-2xl font-bold mb-8 text-tech-cyan">
              Send me a message
            </h3>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-tech-light/80 mb-2 font-medium">
                  Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  className="w-full px-4 py-3 bg-tech-blue/50 border border-tech-cyan/30 rounded-lg focus:outline-none focus:ring-2 focus:ring-tech-cyan focus:border-transparent text-white"
                  placeholder="Your name"
                />
              </div>

              <div>
                <label className="block text-tech-light/80 mb-2 font-medium">
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  className="w-full px-4 py-3 bg-tech-blue/50 border border-tech-cyan/30 rounded-lg focus:outline-none focus:ring-2 focus:ring-tech-cyan focus:border-transparent text-white"
                  placeholder="your.email@example.com"
                />
              </div>

              <div>
                <label className="block text-tech-light/80 mb-2 font-medium">
                  Message
                </label>
                <textarea
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  required
                  rows="5"
                  className="w-full px-4 py-3 bg-tech-blue/50 border border-tech-cyan/30 rounded-lg focus:outline-none focus:ring-2 focus:ring-tech-cyan focus:border-transparent text-white resize-none"
                  placeholder="Tell me about your project..."
                ></textarea>
              </div>

              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full py-4 bg-gradient-to-r from-tech-cyan to-tech-green text-tech-blue font-bold rounded-lg hover:opacity-90 transition-opacity duration-300 disabled:opacity-50"
              >
                {isSubmitting ? 'Sending...' : 'Send Message'}
              </button>

              {submitMessage && (
                <div className={`p-4 rounded-lg ${
                  submitMessage.includes('Thank you') || submitMessage.includes('successfully')
                    ? 'bg-green-500/20 text-green-300' 
                    : 'bg-red-500/20 text-red-300'
                }`}>
                  {submitMessage}
                </div>
              )}
            </form>
          </div>

          {/* Contact Info & WhatsApp */}
          <div className="space-y-8">
            <div className="bg-tech-navy/50 backdrop-blur-sm rounded-2xl p-8 shadow-2xl border border-tech-cyan/20">
              <h3 className="text-2xl font-bold mb-6 text-tech-cyan">
                Direct Contact
              </h3>

              <div className="space-y-6">
                <div className="flex items-center space-x-4">
                  <div className="w-12 h-12 bg-tech-cyan/20 rounded-lg flex items-center justify-center">
                    <svg className="w-6 h-6 text-tech-cyan" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                  </div>
                  <div>
                    <p className="text-tech-light/80">Email</p>
                    <p className="font-semibold text-lg">kelvincharleschacha07@gmail.com</p>
                  </div>
                </div>

                <div className="flex items-center space-x-4">
                  <div className="w-12 h-12 bg-tech-cyan/20 rounded-lg flex items-center justify-center">
                    <svg className="w-6 h-6 text-tech-cyan" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                  <div>
                    <p className="text-tech-light/80">Response Time</p>
                    <p className="font-semibold text-lg">Within 24 hours</p>
                  </div>
                </div>
              </div>
