import React from 'react';

const Services = () => {
  const services = [
    {
      title: "System Architecture & Design",
      description: "Designing scalable, secure, and maintainable system architectures using modern patterns and best practices.",
      icon: "üèóÔ∏è"
    },
    {
      title: "Web & Mobile Application Development",
      description: "Full-stack development of responsive web applications and cross-platform mobile apps using React and React Native.",
      icon: "üì±"
    },
    {
      title: "API Development & Integration",
      description: "Building RESTful and GraphQL APIs with proper documentation, versioning, and integration capabilities.",
      icon: "üîå"
    },
    {
      title: "Security Auditing & Ethical Hacking",
      description: "Comprehensive security assessments, penetration testing, and implementation of security best practices.",
      icon: "üõ°Ô∏è"
    },
    {
      title: "Deployment, Hosting & Server Setup",
      description: "CI/CD pipelines, containerization with Docker, cloud infrastructure setup, and server configuration.",
      icon: "üöÄ"
    },
    {
      title: "Performance Optimization",
      description: "Database optimization, caching strategies, and application performance tuning for maximum efficiency.",
      icon: "‚ö°"
    }
  ];

  return (
    <section id="services" className="px-6 md:px-12 lg:px-24 py-16 md:py-24 bg-gradient-to-b from-white to-gray-50">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold text-tech-blue mb-6">
            My <span className="gradient-text">Services</span>
          </h2>
          <div className="w-24 h-1 bg-gradient-to-r from-tech-cyan to-tech-green mx-auto mb-8"></div>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Comprehensive solutions for building and securing modern digital systems
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div 
              key={index}
              className="bg-white rounded-xl shadow-lg border-t-4 border-tech-cyan overflow-hidden group hover:transform hover:scale-[1.02] hover:shadow-xl transition-all duration-300"
            >
              <div className="p-8">
                <div className="text-4xl mb-6 transform group-hover:scale-110 transition-transform duration-300">
                  {service.icon}
                </div>
                <h3 className="text-xl font-bold text-tech-blue mb-4">
                  {service.title}
                </h3>
                <p className="text-gray-600">
                  {service.description}
                </p>
              </div>
              <div className="px-8 pb-8">
                <div className="inline-flex items-center text-tech-cyan font-semibold group-hover:translate-x-2 transition-transform duration-300">
                  Learn more
                  <svg className="w-5 h-5 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                  </svg>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
