import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:8000/api/contact/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setSubmitMessage(data.message || 'Message sent successfully');
        setFormData({ name: '', email: '', message: '' });
      } else {
        setSubmitMessage('Something went wrong');
      }
    } catch (error) {
      setSubmitMessage('Network error');
    } finally {
      setIsSubmitting(false);
      setTimeout(() => setSubmitMessage(''), 4000);
    }
  };

  const whatsappLink =
    'https://wa.me/255750792039?text=Hi%20Kelvin,%20I%20would%20like%20to%20discuss%20a%20project.';

  return (
    <section
      id="contact"
      className="py-12 px-4 bg-gradient-to-br from-tech-blue to-tech-navy text-white"
    >
      <div className="max-w-4xl mx-auto">

        {/* Header */}
        <div className="text-center mb-10">
          <h2 className="text-2xl md:text-3xl font-bold mb-2">
            Contact <span className="gradient-text">Me</span>
          </h2>
          <p className="text-sm text-tech-light/80">
            Quick & simple ways to reach me
          </p>
        </div>

        {/* Grid */}
        <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">

          {/* WhatsApp Card */}
          <a
            href={whatsappLink}
            target="_blank"
            rel="noopener noreferrer"
            className="bg-gradient-to-r from-green-500 to-green-600
                       rounded-xl p-4 flex items-center gap-3
                       hover:scale-[1.02] transition"
          >
            <div className="w-10 h-10 bg-white/20 rounded-lg flex items-center justify-center">
              <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12.04 2c-5.46 0-9.91 4.45-9.91 9.91 0 1.75.46 3.45 1.32 4.95L2.05 22l5.25-1.38c1.45.79 3.08 1.21 4.74 1.21 5.46 0 9.91-4.45 9.91-9.91 0-5.46-4.45-9.91-9.91-9.91z" />
              </svg>
            </div>
            <div>
              <p className="text-sm font-semibold">WhatsApp</p>
              <p className="text-xs text-white/80">Fast response</p>
            </div>
          </a>

          {/* Email Form */}
          <div className="bg-tech-navy/50 border border-tech-cyan/20
                          rounded-xl p-4">
            <form onSubmit={handleSubmit} className="space-y-2">
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Name"
                required
                className="w-full px-3 py-2 text-sm rounded-md
                           bg-tech-blue/50 border border-tech-cyan/30
                           focus:outline-none text-white"
              />

              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Email"
                required
                className="w-full px-3 py-2 text-sm rounded-md
                           bg-tech-blue/50 border border-tech-cyan/30
                           focus:outline-none text-white"
              />

              <textarea
                name="message"
                value={formData.message}
                onChange={handleChange}
                placeholder="Message"
                rows="2"
                required
                className="w-full px-3 py-2 text-sm rounded-md
                           bg-tech-blue/50 border border-tech-cyan/30
                           focus:outline-none text-white resize-none"
              />

              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full py-2 text-sm font-semibold
                           bg-gradient-to-r from-tech-cyan to-tech-green
                           text-tech-blue rounded-md"
              >
                {isSubmitting ? 'Sending...' : 'Send'}
              </button>

              {submitMessage && (
                <p className="text-xs text-center text-tech-light/80">
                  {submitMessage}
                </p>
              )}
            </form>
          </div>

          {/* Location */}
          <div className="bg-tech-navy/50 border border-tech-cyan/20
                          rounded-xl p-4 flex items-center gap-3">
            <div className="w-10 h-10 bg-tech-cyan/20 rounded-lg flex items-center justify-center">
              üìç
            </div>
            <div>
              <p className="text-sm font-semibold">Location</p>
              <p className="text-xs text-tech-light/80">
                Dodoma, Tanzania
              </p>
            </div>
          </div>

        </div>
      </div>
    </section>
  );
};

export default Contact;
