import React, { useState } from 'react';

const ContactCompact = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:8000/api/contact/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      setSubmitMessage(data.message || 'Message sent!');
      if (response.ok) setFormData({ name: '', email: '', message: '' });
    } catch (error) {
      setSubmitMessage('Network error.');
    } finally {
      setIsSubmitting(false);
      setTimeout(() => setSubmitMessage(''), 5000);
    }
  };

  const contactMethods = [
    { 
      type: 'Email', 
      value: 'kelvincharleschacha07@gmail.com', 
      icon: 'üìß', 
      color: 'text-tech-cyan',
      bg: 'bg-tech-cyan/10'
    },
    { 
      type: 'WhatsApp', 
      value: '+255 750 792 039', 
      icon: 'üí¨', 
      color: 'text-green-400',
      bg: 'bg-green-500/10',
      link: 'https://wa.me/255750792039'
    },
    { 
      type: 'Location', 
      value: 'Available Worldwide', 
      icon: 'üìç', 
      color: 'text-purple-400',
      bg: 'bg-purple-500/10'
    }
  ];

  return (
    <section id="contact" className="py-8 bg-tech-blue text-white">
      <div className="max-w-2xl mx-auto px-4">
        <div className="text-center mb-8">
          <h2 className="text-2xl font-bold mb-2">
            Get in <span className="gradient-text">Touch</span>
          </h2>
          <p className="text-tech-light/60 text-sm">Let's build something amazing</p>
        </div>

        {/* Contact Methods */}
        <div className="grid grid-cols-3 gap-3 mb-6">
          {contactMethods.map((method, index) => (
            method.link ? (
              <a
                key={index}
                href={method.link}
                target="_blank"
                rel="noopener noreferrer"
                className={`${method.bg} rounded-lg p-3 text-center border border-tech-cyan/10 hover:border-tech-cyan/30 transition-colors`}
              >
                <div className={`text-2xl mb-1 ${method.color}`}>{method.icon}</div>
                <div className="text-xs font-medium">{method.type}</div>
              </a>
            ) : (
              <div 
                key={index}
                className={`${method.bg} rounded-lg p-3 text-center border border-tech-cyan/10`}
              >
                <div className={`text-2xl mb-1 ${method.color}`}>{method.icon}</div>
                <div className="text-xs font-medium">{method.type}</div>
              </div>
            )
          ))}
        </div>

        {/* Contact Form */}
        <div className="bg-tech-navy/30 rounded-xl p-5 border border-tech-cyan/10">
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              className="w-full px-3 py-2.5 bg-tech-blue/30 border border-tech-cyan/20 rounded-lg text-white text-sm"
              placeholder="Your Name"
            />

            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full px-3 py-2.5 bg-tech-blue/30 border border-tech-cyan/20 rounded-lg text-white text-sm"
              placeholder="Your Email"
            />

            <textarea
              name="message"
              value={formData.message}
              onChange={handleChange}
              required
              rows="3"
              className="w-full px-3 py-2.5 bg-tech-blue/30 border border-tech-cyan/20 rounded-lg text-white text-sm resize-none"
              placeholder="Your Message"
            ></textarea>

            <button
              type="submit"
              disabled={isSubmitting}
              className="w-full py-2.5 bg-gradient-to-r from-tech-cyan to-tech-green text-tech-blue font-bold rounded-lg hover:shadow-md transition-shadow"
            >
              {isSubmitting ? 'Sending...' : 'Send Message'}
            </button>
          </form>

          {submitMessage && (
            <div className={`mt-3 p-2 rounded text-xs ${
              submitMessage.includes('success') ? 'bg-green-500/10 text-green-300' : 'bg-red-500/10 text-red-300'
            }`}>
              {submitMessage}
            </div>
          )}
        </div>

        {/* Social Links */}
        <div className="mt-6 flex justify-center gap-4">
          <a href="https://github.com/kelvinchacha" target="_blank" className="text-tech-light/60 hover:text-tech-cyan">
            <i className="devicon-github-plain text-xl"></i>
          </a>
          <a href="https://linkedin.com/in/kelvinchacha" target="_blank" className="text-tech-light/60 hover:text-tech-cyan">
            <i className="devicon-linkedin-plain text-xl"></i>
          </a>
        </div>
      </div>
    </section>
  );
};

export default ContactCompact;
