import React, { useState, useEffect } from 'react';
import { Code, Cpu, Shield, Zap, ChevronDown, Sparkles, Globe } from 'lucide-react';

const Hero = () => {
  const [typedText, setTypedText] = useState('');
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);

  const words = ['Scalable', 'Secure', 'Maintainable', 'Performant', 'Modern'];

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  // Typing effect
  useEffect(() => {
    const handleTyping = () => {
      const i = currentWordIndex % words.length;
      const fullWord = words[i];

      setTypedText(isDeleting 
        ? fullWord.substring(0, typedText.length - 1)
        : fullWord.substring(0, typedText.length + 1)
      );

      if (!isDeleting && typedText === fullWord) {
        setTimeout(() => setIsDeleting(true), 1500);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setCurrentWordIndex(currentWordIndex + 1);
        if (currentWordIndex === words.length - 1) {
          setLoopNum(loopNum + 1);
        }
      }
    };

    const timer = setTimeout(handleTyping, isDeleting ? 50 : 100);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, currentWordIndex, words]);

  // Floating elements effect
  const FloatingIcon = ({ icon: Icon, delay, position }) => (
    <div 
      className={`absolute ${position} animate-float`}
      style={{ animationDelay: `${delay}s` }}
    >
      <Icon className="w-6 h-6 text-tech-cyan/30" />
    </div>
  );

  return (
    <section 
      id="hero" 
      className="relative min-h-screen flex items-center justify-start bg-tech-blue text-white overflow-hidden"
      style={{
        backgroundImage: 'linear-gradient(rgba(10, 25, 47, 0.95), rgba(10, 25, 47, 0.98)), radial-gradient(circle at 20% 50%, rgba(0, 212, 255, 0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(0, 255, 157, 0.08) 0%, transparent 50%)',
        backgroundSize: 'cover, 200% 200%, 200% 200%',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        animation: 'gradientShift 15s ease infinite'
      }}
    >
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-1/4 left-1/4 w-64 h-64 bg-tech-cyan/5 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-tech-green/3 rounded-full blur-3xl animate-pulse delay-1000"></div>

        {/* Grid pattern */}
        <div className="absolute inset-0 opacity-5" style={{
          backgroundImage: `linear-gradient(to right, #00d4ff 1px, transparent 1px),
                           linear-gradient(to bottom, #00d4ff 1px, transparent 1px)`,
          backgroundSize: '50px 50px'
        }}></div>

        {/* Floating icons */}
        <FloatingIcon icon={Code} delay={0} position="top-20 left-10" />
        <FloatingIcon icon={Cpu} delay={1} position="top-40 right-20" />
        <FloatingIcon icon={Shield} delay={2} position="bottom-40 left-20" />
        <FloatingIcon icon={Zap} delay={3} position="bottom-20 right-32" />
        <FloatingIcon icon={Globe} delay={4} position="top-32 right-1/4" />
      </div>

      {/* Glowing orbs */}
      <div className="absolute top-1/3 left-1/4 w-3 h-3">
        <div className="absolute w-full h-full bg-tech-cyan rounded-full animate-ping opacity-20"></div>
        <div className="w-full h-full bg-tech-cyan rounded-full shadow-glow"></div>
      </div>

      <div className="px-6 md:px-12 lg:px-24 py-16 md:py-24 max-w-7xl mx-auto w-full relative z-10">
        <div className="max-w-4xl">
          {/* Badge with animation */}
          <div className="inline-flex items-center gap-2 mb-8 px-4 py-2 bg-tech-navy/50 backdrop-blur-sm rounded-full border border-tech-cyan/30 animate-fade-in">
            <Sparkles className="w-4 h-4 text-tech-cyan animate-spin-slow" />
            <span className="text-tech-cyan font-mono text-sm tracking-wider">
              AVAILABLE FOR CONSULTATION
            </span>
          </div>

          {/* Main heading with gradient animation */}
          <div className="mb-6 overflow-hidden">
            <div className="flex items-center gap-4 mb-4 animate-slide-up">
              <div className="w-12 h-0.5 bg-gradient-to-r from-tech-cyan to-tech-green"></div>
              <span className="text-tech-cyan font-mono text-lg tracking-wider animate-typing">
                Hi, I'm Kelvin Chacha
              </span>
            </div>
          </div>

          <h1 className="text-6xl md:text-8xl lg:text-9xl font-bold mb-6 leading-none">
            <span className="gradient-text animate-gradient">KELVIN</span>
            <br />
            <span className="text-tech-light">CHACHA</span>
          </h1>

          {/* Animated title */}
          <div className="mb-8">
            <h2 className="text-4xl md:text-6xl font-bold mb-4 text-tech-light">
              <span className="inline-flex items-center">
                <Cpu className="w-8 h-8 md:w-12 md:h-12 mr-4 text-tech-cyan animate-float" />
                System Architect
              </span>
              <br />
              <span className="inline-flex items-center mt-2">
                <Code className="w-8 h-8 md:w-12 md:h-12 mr-4 text-tech-green animate-float delay-500" />
                <span className="gradient-text animate-gradient-slow">& Full-Stack Developer</span>
              </span>
            </h2>
          </div>

          {/* Dynamic typing text */}
          <div className="mb-10 max-w-3xl">
            <p className="text-2xl md:text-3xl text-tech-light/90 mb-6">
              I build <span className="text-tech-cyan font-bold animate-pulse">{typedText}</span>
              <span className="inline-block w-1 h-8 ml-1 bg-tech-cyan animate-blink">|</span>
            </p>
            <p className="text-xl text-tech-light/70 leading-relaxed">
              Specializing in enterprise-grade solutions using Django, React, 
              and cloud-native architectures. I transform complex requirements 
              into elegant, high-performance systems.
            </p>
          </div>

          {/* Stats row */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-12 max-w-2xl">
            {[
              { value: '50+', label: 'Projects', icon: 'ðŸš€' },
              { value: '5+', label: 'Years Exp', icon: 'â³' },
              { value: '100%', label: 'Satisfaction', icon: 'â­' },
              { value: '24/7', label: 'Support', icon: 'ðŸ›¡ï¸' }
            ].map((stat, index) => (
              <div 
                key={index}
                className="bg-tech-navy/40 backdrop-blur-sm p-4 rounded-xl border border-tech-cyan/20 hover:border-tech-cyan/50 transition-all duration-300 hover:transform hover:scale-105 group"
              >
                <div className="flex items-center gap-2 mb-2">
                  <span className="text-2xl">{stat.icon}</span>
                  <span className="text-3xl font-bold text-tech-cyan group-hover:text-tech-green transition-colors">
                    {stat.value}
                  </span>
                </div>
                <p className="text-sm text-tech-light/70">{stat.label}</p>
              </div>
            ))}
          </div>

          {/* CTA Buttons with hover effects */}
          <div className="flex flex-wrap gap-6">
            <button
              onClick={() => scrollToSection('contact')}
              className="group relative px-10 py-5 bg-gradient-to-r from-tech-cyan via-tech-cyan to-tech-green text-tech-blue font-bold rounded-2xl hover:shadow-2xl hover:shadow-tech-cyan/30 transition-all duration-500 hover:scale-105 overflow-hidden"
            >
              <span className="relative z-10 flex items-center gap-3">
                <Zap className="w-5 h-5" />
                Hire Me
                <ChevronDown className="w-5 h-5 group-hover:translate-y-1 transition-transform" />
              </span>
              <div className="absolute inset-0 bg-gradient-to-r from-tech-green to-tech-cyan opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
            </button>

            <button
              onClick={() => scrollToSection('skills')}
              className="group px-10 py-5 border-2 border-tech-cyan text-tech-cyan font-bold rounded-2xl hover:bg-tech-cyan/10 transition-all duration-500 hover:scale-105 hover:shadow-xl hover:shadow-tech-cyan/20 relative overflow-hidden"
            >
              <span className="relative z-10 flex items-center gap-3">
                <Shield className="w-5 h-5" />
                View My Skills
              </span>
              <div className="absolute inset-0 bg-tech-cyan/5 transform -translate-x-full group-hover:translate-x-0 transition-transform duration-700"></div>
            </button>

            {/* Download CV button */}
            <button
              onClick={() => window.open('/kelvin-chacha-cv.pdf', '_blank')}
              className="group px-8 py-5 bg-tech-navy/50 backdrop-blur-sm border border-tech-light/20 text-tech-light font-semibold rounded-2xl hover:border-tech-cyan transition-all duration-300 hover:scale-105"
            >
              <span className="flex items-center gap-3">
                ðŸ“„ Download CV
                <span className="text-tech-cyan opacity-0 group-hover:opacity-100 transition-opacity">
                  â†’
                </span>
              </span>
            </button>
          </div>

          {/* Tech stack badges */}
          <div className="mt-16 max-w-3xl">
            <p className="text-tech-light/60 mb-4 font-mono text-sm">TECH STACK</p>
            <div className="flex flex-wrap gap-3">
              {['Django', 'React', 'PostgreSQL', 'AWS', 'Docker', 'Kubernetes', 'Python', 'TypeScript'].map((tech, index) => (
                <div 
                  key={index}
                  className="px-4 py-2 bg-tech-navy/30 backdrop-blur-sm rounded-full border border-tech-cyan/10 hover:border-tech-cyan/50 transition-all duration-300 hover:scale-110 group"
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <span className="text-tech-light/80 group-hover:text-tech-cyan transition-colors font-mono text-sm">
                    {tech}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced scroll indicator */}
      <div className="absolute bottom-12 left-1/2 transform -translate-x-1/2 z-10">
        <div 
          className="animate-bounce cursor-pointer group"
          onClick={() => scrollToSection('about')}
        >
          <div className="relative">
            <div className="w-8 h-14 border-2 border-tech-cyan/50 rounded-full flex justify-center group-hover:border-tech-cyan transition-colors">
              <div className="w-1 h-4 bg-tech-cyan rounded-full mt-3 animate-scroll"></div>
            </div>
            <div className="absolute -bottom-8 left-1/2 transform -translate-x-1/2 text-tech-cyan/50 text-sm font-mono group-hover:text-tech-cyan transition-colors">
              Scroll
            </div>
          </div>
        </div>
      </div>

      {/* Social links floating */}
      <div className="absolute right-8 top-1/2 transform -translate-y-1/2 hidden lg:flex flex-col gap-6">
        {[
          { icon: 'ðŸ‘¨â€ðŸ’»', label: 'GitHub', url: 'https://github.com/kelvinchacha' },
          { icon: 'ðŸ’¼', label: 'LinkedIn', url: 'https://linkedin.com/in/kelvinchacha' },
          { icon: 'ðŸ¦', label: 'Twitter', url: 'https://twitter.com/kelvinchacha' },
          { icon: 'ðŸ“§', label: 'Email', url: 'mailto:kelvincharleschacha07@gmail.com' }
        ].map((social, index) => (
          <a
            key={index}
            href={social.url}
            target="_blank"
            rel="noopener noreferrer"
            className="group w-12 h-12 flex items-center justify-center bg-tech-navy/30 backdrop-blur-sm rounded-full border border-tech-cyan/10 hover:border-tech-cyan hover:bg-tech-cyan/10 transition-all duration-300 hover:scale-110 hover:shadow-lg hover:shadow-tech-cyan/20"
            style={{ animationDelay: `${index * 200}ms` }}
          >
            <span className="text-xl group-hover:scale-110 transition-transform">
              {social.icon}
            </span>
            <span className="absolute right-full mr-4 px-2 py-1 bg-tech-navy text-tech-light text-sm rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
              {social.label}
            </span>
          </a>
        ))}
      </div>
    </section>
  );
};

export default Hero;
