import React from 'react';

const AboutTimeline = () => {
  const timeline = [
    {
      year: "2019",
      title: "Started Career",
      description: "Began as Full-Stack Developer",
      icon: "üöÄ"
    },
    {
      year: "2020",
      title: "System Architecture",
      description: "Focused on scalable systems",
      icon: "üèóÔ∏è"
    },
    {
      year: "2022",
      title: "Security Specialization",
      description: "Added security auditing",
      icon: "üõ°Ô∏è"
    },
    {
      year: "Now",
      title: "Consulting",
      description: "Architecture & development",
      icon: "üíº"
    }
  ];

  return (
    <section id="about" className="py-10 bg-white">
      <div className="max-w-3xl mx-auto px-4">
        <div className="text-center mb-8">
          <h2 className="text-2xl font-bold text-tech-blue">
            <span className="gradient-text">Kelvin Chacha</span>
          </h2>
          <p className="text-gray-500 text-sm mt-2">System Architect & Full-Stack Developer</p>
        </div>

        {/* Bio */}
        <div className="mb-8 p-4 bg-gray-50 rounded-lg">
          <p className="text-gray-700 text-sm">
            Specializing in Django, React, and cloud architecture. 
            Building secure, scalable systems for modern businesses.
          </p>
        </div>

        {/* Timeline */}
        <div className="space-y-4">
          {timeline.map((item, index) => (
            <div key={index} className="flex items-start gap-4">
              <div className="flex-shrink-0 w-12 h-12 flex items-center justify-center bg-tech-blue/10 rounded-full">
                <span className="text-lg">{item.icon}</span>
              </div>
              <div>
                <div className="flex items-center gap-2">
                  <span className="font-bold text-tech-blue">{item.year}</span>
                  <span className="text-gray-800 font-medium">{item.title}</span>
                </div>
                <p className="text-gray-600 text-sm mt-1">{item.description}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Values */}
        <div className="mt-8 grid grid-cols-2 gap-3">
          {[
            { icon: "‚ö°", title: "Fast", desc: "Quick delivery" },
            { icon: "üîí", title: "Secure", desc: "Safe systems" },
            { icon: "üìà", title: "Scalable", desc: "Grows with you" },
            { icon: "üíé", title: "Quality", desc: "Clean code" }
          ].map((value, index) => (
            <div key={index} className="p-3 bg-gray-50 rounded-lg text-center">
              <div className="text-xl mb-1">{value.icon}</div>
              <div className="font-medium text-gray-800">{value.title}</div>
              <div className="text-xs text-gray-500">{value.desc}</div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default AboutTimeline;
