import React from 'react';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <section 
      id="hero" 
      className="relative min-h-screen flex items-center justify-start bg-tech-blue text-white"
      style={{
        backgroundImage: 'linear-gradient(rgba(10, 25, 47, 0.9), rgba(10, 25, 47, 0.9)), url("https://images.unsplash.com/photo-1555066931-4365d14bab8c?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed'
      }}
    >
      <div className="px-6 md:px-12 lg:px-24 py-16 md:py-24 max-w-7xl mx-auto w-full">
        <div className="max-w-3xl">
          <div className="mb-6">
            <span className="text-tech-cyan font-mono text-lg">Hi, I'm</span>
          </div>

          <h1 className="text-5xl md:text-7xl font-bold mb-4">
            Kelvin Chacha
          </h1>

          <h2 className="text-3xl md:text-5xl font-bold mb-6 text-tech-light">
            System Architect &<br />
            <span className="gradient-text">Full-Stack Developer</span>
          </h2>

          <p className="text-xl text-tech-light/80 mb-10 max-w-2xl">
            I build secure, scalable, and maintainable systems using modern technologies.
            Specializing in Django, React, and cloud-ready architectures.
          </p>

          <div className="flex flex-wrap gap-4">
            <button
              onClick={() => scrollToSection('contact')}
              className="px-8 py-4 bg-tech-cyan text-tech-blue font-semibold rounded-lg hover:bg-tech-cyan/90 transition-colors duration-300 shadow-lg hover:shadow-xl"
            >
              Hire Me
            </button>

            <button
              onClick={() => scrollToSection('skills')}
              className="px-8 py-4 border-2 border-tech-cyan text-tech-cyan font-semibold rounded-lg hover:bg-tech-cyan/10 transition-colors duration-300"
            >
              My Skills
            </button>
          </div>
        </div>
      </div>

      {/* Scroll indicator */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2">
        <div className="animate-bounce cursor-pointer" onClick={() => scrollToSection('about')}>
          <div className="w-6 h-10 border-2 border-tech-cyan rounded-full flex justify-center">
            <div className="w-1 h-3 bg-tech-cyan rounded-full mt-2"></div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
