import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState({ type: '', message: '' });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus({ type: '', message: '' });

    try {
      const response = await fetch('http://localhost:8000/api/contact/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setSubmitStatus({ 
          type: 'success', 
          message: 'Your message has been sent successfully! I will get back to you soon.' 
        });
        setFormData({ name: '', email: '', message: '' });
      } else {
        setSubmitStatus({ 
          type: 'error', 
          message: data.message || 'Something went wrong. Please try again.' 
        });
      }
    } catch (error) {
      setSubmitStatus({ 
        type: 'error', 
        message: 'Connection error. Please try the WhatsApp option or email directly.' 
      });
    } finally {
      setIsSubmitting(false);
      setTimeout(() => setSubmitStatus({ type: '', message: '' }), 5000);
    }
  };

  const contactInfo = [
    {
      title: "Email",
      details: "kelvincharleschacha07@gmail.com",
      description: "For detailed project discussions",
      icon: (
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
        </svg>
      ),
      color: "text-cyan-400",
      bgColor: "bg-cyan-400/10",
      borderColor: "border-cyan-400/20"
    },
    {
      title: "Phone",
      details: "+255 750 792 039",
      description: "Call for urgent inquiries",
      icon: (
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
        </svg>
      ),
      color: "text-green-400",
      bgColor: "bg-green-400/10",
      borderColor: "border-green-400/20"
    },
    {
      title: "Response Time",
      details: "Within 24 Hours",
      description: "Quick turnaround guaranteed",
      icon: (
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      color: "text-yellow-400",
      bgColor: "bg-yellow-400/10",
      borderColor: "border-yellow-400/20"
    },
    {
      title: "Location",
      details: "Available Worldwide",
      description: "Remote & on-site projects",
      icon: (
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
        </svg>
      ),
      color: "text-purple-400",
      bgColor: "bg-purple-400/10",
      borderColor: "border-purple-400/20"
    }
  ];

  const socialLinks = [
    {
      icon: "devicon-github-plain",
      label: "GitHub",
      url: "https://github.com/kelvinchacha",
      color: "hover:text-gray-300",
      bgColor: "hover:bg-gray-800"
    },
    {
      icon: "devicon-linkedin-plain",
      label: "LinkedIn",
      url: "https://linkedin.com/in/kelvinchacha",
      color: "hover:text-blue-400",
      bgColor: "hover:bg-blue-900/30"
    },
    {
      icon: "devicon-whatsapp-plain",
      label: "WhatsApp",
      url: "https://wa.me/255750792039",
      color: "hover:text-green-400",
      bgColor: "hover:bg-green-900/30"
    }
  ];

  const SendIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
    </svg>
  );

  const SuccessIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
    </svg>
  );

  const ErrorIcon = () => (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
    </svg>
  );

  const Spinner = () => (
    <svg className="w-5 h-5 animate-spin" fill="none" viewBox="0 0 24 24">
      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
    </svg>
  );

  return (
    <section id="contact" className="py-12 md:py-16 bg-tech-navy text-white">
      <div className="max-w-6xl mx-auto px-4 sm:px-6">
        {/* Header */}
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Let's <span className="text-tech-cyan">Build Together</span>
          </h2>
          <div className="w-20 h-1 bg-gradient-to-r from-tech-cyan to-tech-green mx-auto mb-6"></div>
          <p className="text-tech-light/70 max-w-2xl mx-auto text-sm md:text-base">
            Have a project in mind? Let's discuss how we can transform your ideas into scalable, secure solutions.
          </p>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Contact Information */}
          <div className="lg:col-span-1">
            <div className="bg-tech-blue/30 backdrop-blur-sm rounded-xl p-6 border border-tech-cyan/10 mb-6">
              <h3 className="text-xl font-bold mb-6 text-tech-light flex items-center gap-2">
                <svg className="w-5 h-5 text-tech-cyan" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
                <span>Contact Information</span>
              </h3>

              <div className="space-y-4">
                {contactInfo.map((info, index) => (
                  <div 
                    key={index}
                    className={`${info.bgColor} ${info.borderColor} border rounded-lg p-4 transition-all duration-300 hover:scale-[1.02]`}
                  >
                    <div className="flex items-start gap-3">
                      <div className={`${info.color} mt-1`}>
                        {info.icon}
                      </div>
                      <div className="flex-1">
                        <h4 className="font-bold text-tech-light mb-1">{info.title}</h4>
                        <p className="text-tech-light/90 text-sm mb-1">{info.details}</p>
                        <p className="text-tech-light/50 text-xs">{info.description}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Social Links */}
            <div className="bg-tech-blue/30 backdrop-blur-sm rounded-xl p-6 border border-tech-cyan/10">
              <h3 className="text-xl font-bold mb-6 text-tech-light">Connect With Me</h3>
              <div className="flex flex-wrap gap-3">
                {socialLinks.map((social, index) => (
                  <a
                    key={index}
                    href={social.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`group flex items-center gap-2 px-4 py-3 bg-tech-navy/50 rounded-lg border border-tech-cyan/10 ${social.bgColor} ${social.color} transition-all duration-300 hover:scale-105`}
                  >
                    <i className={`${social.icon} text-lg`}></i>
                    <span className="text-sm font-medium">{social.label}</span>
                    <svg className="w-3 h-3 opacity-0 group-hover:opacity-100 group-hover:translate-x-1 transition-all" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </a>
                ))}
              </div>
            </div>
          </div>

          {/* Contact Form */}
          <div className="lg:col-span-2">
            <div className="bg-tech-blue/20 backdrop-blur-sm rounded-xl p-6 md:p-8 border border-tech-cyan/10 h-full">
              <div className="flex items-center gap-3 mb-6">
                <div className="w-12 h-12 flex items-center justify-center bg-gradient-to-br from-tech-cyan/20 to-tech-green/20 rounded-lg">
                  <SendIcon />
                </div>
                <div>
                  <h3 className="text-2xl font-bold text-tech-light">Send a Message</h3>
                  <p className="text-tech-light/60 text-sm">Fill out the form below and I'll respond promptly</p>
                </div>
              </div>

              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-tech-light/80 text-sm font-medium mb-2">
                      Your Name
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                      className="w-full px-4 py-3 bg-tech-navy/50 border border-tech-cyan/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-tech-cyan focus:border-transparent text-white placeholder:text-tech-light/40"
                      placeholder="John Doe"
                    />
                  </div>

                  <div>
                    <label className="block text-tech-light/80 text-sm font-medium mb-2">
                      Email Address
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      className="w-full px-4 py-3 bg-tech-navy/50 border border-tech-cyan/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-tech-cyan focus:border-transparent text-white placeholder:text-tech-light/40"
                      placeholder="john@example.com"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-tech-light/80 text-sm font-medium mb-2">
                    Project Details
                  </label>
                  <textarea
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    rows="6"
                    className="w-full px-4 py-3 bg-tech-navy/50 border border-tech-cyan/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-tech-cyan focus:border-transparent text-white placeholder:text-tech-light/40 resize-none"
                    placeholder="Tell me about your project, timeline, budget, and any specific requirements..."
                  ></textarea>
                </div>

                <div className="flex items-center justify-between">
                  <div>
                    {submitStatus.type && (
                      <div className={`flex items-center gap-2 px-4 py-2 rounded-lg ${
                        submitStatus.type === 'success' 
                          ? 'bg-green-500/10 text-green-400 border border-green-500/20' 
                          : 'bg-red-500/10 text-red-400 border border-red-500/20'
                      }`}>
                        {submitStatus.type === 'success' ? <SuccessIcon /> : <ErrorIcon />}
                        <span className="text-sm">{submitStatus.message}</span>
                      </div>
                    )}
                  </div>

                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="group relative px-8 py-3 bg-gradient-to-r from-tech-cyan to-tech-green text-tech-blue font-bold rounded-lg hover:shadow-xl hover:shadow-tech-cyan/30 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <span className="flex items-center gap-2">
                      {isSubmitting ? (
                        <>
                          <Spinner />
                          <span>Processing...</span>
                        </>
                      ) : (
                        <>
                          <span>Send Message</span>
                          <SendIcon />
                        </>
                      )}
                    </span>
                  </button>
                </div>
              </form>

              <div className="mt-8 pt-6 border-t border-tech-cyan/10">
                <p className="text-tech-light/50 text-sm text-center">
                  Your information is secure and will only be used to respond to your inquiry.
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="mt-10 text-center">
          <div className="inline-flex flex-col sm:flex-row items-center gap-4 bg-gradient-to-r from-tech-cyan/10 to-tech-green/10 rounded-2xl p-6 border border-tech-cyan/20">
            <div className="text-left">
              <h4 className="text-lg font-bold text-tech-light mb-1">Ready to Start Your Project?</h4>
              <p className="text-tech-light/60 text-sm">Let's schedule a consultation call to discuss your requirements.</p>
            </div>
            <a
              href="https://wa.me/255750792039"
              target="_blank"
              rel="noopener noreferrer"
              className="px-6 py-3 bg-tech-cyan text-tech-blue font-bold rounded-lg hover:bg-tech-cyan/90 transition-colors whitespace-nowrap"
            >
              Schedule a Call
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
