import React, { useState, useEffect, useRef } from 'react';
import Particles from 'react-tsparticles';
import { loadSlim } from 'tsparticles-slim';

const Hero = () => {
  const [typedText, setTypedText] = useState('');
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isMobile, setIsMobile] = useState(false);

  const words = ['Scalable', 'Secure', 'Maintainable', 'Fast', 'Reliable'];

  // Check if mobile
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  const scrollToSection = (sectionId) => {
    document.getElementById(sectionId)?.scrollIntoView({ behavior: 'smooth' });
  };

  // Typing effect
  useEffect(() => {
    const handleTyping = () => {
      const i = currentWordIndex % words.length;
      const fullWord = words[i];

      setTypedText(isDeleting 
        ? fullWord.substring(0, typedText.length - 1)
        : fullWord.substring(0, typedText.length + 1)
      );

      if (!isDeleting && typedText === fullWord) {
        setTimeout(() => setIsDeleting(true), 1500);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setCurrentWordIndex(currentWordIndex + 1);
      }
    };

    const timer = setTimeout(handleTyping, isDeleting ? 50 : 100);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, currentWordIndex]);

  // Mouse tracking (desktop only)
  useEffect(() => {
    if (isMobile) return;

    const handleMouseMove = (e) => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [isMobile]);

  // Particle configuration
  const particlesInit = async (engine) => {
    await loadSlim(engine);
  };

  const particlesOptions = {
    background: {
      color: {
        value: "transparent",
      },
    },
    fpsLimit: 60,
    interactivity: {
      events: {
        onHover: {
          enable: !isMobile,
          mode: "repulse",
        },
        resize: true,
      },
      modes: {
        repulse: {
          distance: 100,
          duration: 0.4,
        },
      },
    },
    particles: {
      color: {
        value: ["#00d4ff", "#00ff9d", "#ccd6f6"],
      },
      links: {
        color: "#00d4ff",
        distance: 150,
        enable: true,
        opacity: 0.1,
        width: 1,
      },
      collisions: {
        enable: true,
      },
      move: {
        direction: "none",
        enable: true,
        outModes: {
          default: "bounce",
        },
        random: true,
        speed: 0.5,
        straight: false,
      },
      number: {
        density: {
          enable: true,
          area: isMobile ? 400 : 800,
        },
        value: isMobile ? 20 : 40,
      },
      opacity: {
        value: 0.3,
      },
      shape: {
        type: ["circle", "triangle"],
      },
      size: {
        value: { min: 1, max: 3 },
      },
    },
    detectRetina: true,
  };

  return (
    <section 
      id="hero" 
      className="relative min-h-screen flex items-center justify-center bg-tech-blue text-white overflow-hidden"
    >
      {/* CODE EDITOR BACKGROUND */}
      <div className="absolute inset-0 z-0 overflow-hidden">
        {/* Dark base */}
        <div className="absolute inset-0 bg-gradient-to-br from-tech-blue via-tech-navy to-tech-blue"></div>

        {/* Computer monitor effect */}
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-full max-w-4xl h-3/5 bg-tech-dark rounded-xl border-2 border-tech-cyan/30 shadow-2xl">
          {/* Monitor bezel */}
          <div className="absolute inset-0 rounded-xl overflow-hidden">
            {/* Screen glow */}
            <div className="absolute inset-0 bg-gradient-to-b from-tech-cyan/5 to-transparent"></div>

            {/* Code editor UI */}
            <div className="absolute top-0 left-0 right-0 h-8 bg-tech-navy/80 border-b border-tech-cyan/20 flex items-center px-4">
              <div className="flex gap-2">
                <div className="w-3 h-3 rounded-full bg-red-500"></div>
                <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
                <div className="w-3 h-3 rounded-full bg-green-500"></div>
              </div>
              <div className="ml-4 text-tech-light/50 text-sm font-mono">portfolio.jsx</div>
            </div>

            {/* Animated code */}
            <div className="absolute inset-0 pt-12 px-8 font-mono text-sm">
              <div className="text-tech-cyan/40">// Kelvin Chacha - System Architecture</div>
              <div className="text-tech-green/40">import</div>
              <div className="text-tech-light/30 ml-4">React,</div>
              <div className="text-tech-light/30 ml-4">Django,</div>
              <div className="text-tech-light/30 ml-4">Cloud</div>
              <div>&nbsp;</div>
              <div className="text-tech-cyan/40">export default</div>
              <div className="text-tech-green/40 ml-4">function</div>
              <div className="text-tech-light/30">Architecture()</div>
              <div className="text-tech-light/30">{'{'}</div>
              <div className="text-tech-green/40 ml-8">return</div>
              <div className="text-tech-light/30 ml-12">&lt;</div>
              <div className="text-tech-cyan/40">System</div>
              <div className="text-tech-light/30">&gt;</div>
              <div className="text-tech-light/30 ml-16">&lt;</div>
              <div className="text-tech-green/40">Scalable</div>
              <div className="text-tech-light/30">/&gt;</div>
              <div className="text-tech-light/30 ml-16">&lt;</div>
              <div className="text-tech-green/40">Secure</div>
              <div className="text-tech-light/30">/&gt;</div>
              <div className="text-tech-light/30 ml-16">&lt;</div>
              <div className="text-tech-green/40">Maintainable</div>
              <div className="text-tech-light/30">/&gt;</div>
              <div className="text-tech-light/30 ml-12">&lt;/</div>
              <div className="text-tech-cyan/40">System</div>
              <div className="text-tech-light/30">&gt;</div>
              <div className="text-tech-light/30 ml-8">{'}'}</div>
              <div>&nbsp;</div>
              <div className="text-tech-cyan/40">// Running on port 3000</div>
              <div className="text-tech-green/40">$</div>
              <div className="text-tech-light/30 ml-2">npm run deploy</div>
            </div>

            {/* Blinking cursor */}
            <div className="absolute top-24 left-48 w-2 h-5 bg-tech-cyan animate-blink"></div>

            {/* Status bar */}
            <div className="absolute bottom-0 left-0 right-0 h-6 bg-tech-navy/80 border-t border-tech-cyan/20 flex items-center px-4 text-tech-light/30 text-xs font-mono">
              <div>Ln 24, Col 12</div>
              <div className="ml-auto">UTF-8</div>
              <div className="ml-4">JavaScript React</div>
            </div>
          </div>

          {/* Monitor stand */}
          <div className="absolute -bottom-4 left-1/2 transform -translate-x-1/2 w-32 h-4 bg-tech-cyan/20 rounded-b-lg"></div>
          <div className="absolute -bottom-8 left-1/2 transform -translate-x-1/2 w-16 h-4 bg-tech-cyan/10 rounded-b-lg"></div>
        </div>

        {/* Floating code fragments */}
        <div className="absolute top-20 right-20 text-tech-cyan/10 font-mono text-lg">{'{'}</div>
        <div className="absolute bottom-20 left-20 text-tech-green/10 font-mono text-lg">}</div>
        <div className="absolute top-1/3 left-1/4 text-tech-light/5 font-mono">=&gt;</div>
      </div>

      {/* Interactive Cursor Effect (Desktop only) */}
      {!isMobile && (
        <>
          <div 
            className="fixed z-50 pointer-events-none mix-blend-difference"
            style={{
              left: `${mousePosition.x}px`,
              top: `${mousePosition.y}px`,
              transform: 'translate(-50%, -50%)'
            }}
          >
            <div className="w-6 h-6 border border-tech-cyan rounded-full animate-ping opacity-30"></div>
            <div className="absolute w-4 h-4 bg-tech-cyan rounded-full blur-sm"></div>
          </div>

          {/* Mouse trail effect */}
          <div className="fixed inset-0 pointer-events-none z-40">
            {[...Array(3)].map((_, i) => (
              <div
                key={i}
                className="absolute w-2 h-2 bg-tech-cyan/20 rounded-full"
                style={{
                  left: `${mousePosition.x + (i * 8)}px`,
                  top: `${mousePosition.y + (i * 8)}px`,
                  transition: `transform ${0.1 * (i + 1)}s linear, opacity ${0.1 * (i + 1)}s linear`,
                  transform: 'translate(-50%, -50%)',
                  opacity: 0.5 - (i * 0.15)
                }}
              />
            ))}
          </div>
        </>
      )}

      {/* Particle Background */}
      <div className="absolute inset-0 z-1">
        <Particles
          id="tsparticles"
          init={particlesInit}
          options={particlesOptions}
          className="absolute inset-0"
        />
      </div>

      {/* Main Content */}
      <div className="relative z-10 text-center px-4 sm:px-6 md:px-8 max-w-4xl mx-auto w-full">
        {/* Animated Badge */}
        <div className="inline-flex items-center gap-2 mb-6 sm:mb-8 px-3 sm:px-4 py-1.5 sm:py-2 bg-tech-navy/40 backdrop-blur-sm rounded-full border border-tech-cyan/30 animate-fade-in">
          <span className="text-tech-cyan animate-pulse text-sm sm:text-base">‚ö°</span>
          <span className="text-tech-cyan font-mono text-xs sm:text-sm tracking-wider">
            SYSTEM ARCHITECT & FULL-STACK DEV
          </span>
        </div>

        {/* Main Name */}
        <div className="relative mb-4 sm:mb-6">
          <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold">
            <span className="gradient-text block">Kelvin Chacha</span>
          </h1>
        </div>

        {/* Typing Animation */}
        <div className="mb-6 sm:mb-8 px-2">
          <p className="text-lg sm:text-xl md:text-2xl text-tech-light/90 mb-2 sm:mb-4">
            Building <span className="text-tech-cyan font-bold">{typedText}</span>
            <span className="inline-block w-1 h-6 sm:h-8 ml-1 bg-tech-cyan animate-blink">|</span>
          </p>
          <p className="text-sm sm:text-base md:text-lg text-tech-light/70 max-w-2xl mx-auto px-2">
            Enterprise-grade systems with Django, React, and cloud architecture.
            Transforming complexity into elegant, high-performance solutions.
          </p>
        </div>

        {/* CTA Buttons */}
        <div className="flex flex-col sm:flex-row gap-3 sm:gap-4 justify-center items-center mb-6 sm:mb-8 px-2">
          <button
            onClick={() => scrollToSection('contact')}
            className="group relative px-6 sm:px-8 py-3 sm:py-4 bg-gradient-to-r from-tech-cyan to-tech-green text-tech-blue font-bold rounded-lg sm:rounded-xl hover:shadow-xl hover:shadow-tech-cyan/30 transition-all duration-300 hover:scale-105 w-full sm:w-auto max-w-xs"
          >
            <span className="relative z-10 flex items-center justify-center gap-2">
              <span className="text-lg">üíº</span>
              <span>Hire Me</span>
              <span className="group-hover:translate-x-1 transition-transform hidden sm:inline">‚Üí</span>
            </span>
          </button>

          <button
            onClick={() => scrollToSection('skills')}
            className="group px-6 sm:px-8 py-3 sm:py-4 bg-tech-navy/40 backdrop-blur-sm border border-tech-cyan/30 text-tech-cyan font-bold rounded-lg sm:rounded-xl hover:bg-tech-cyan/10 transition-all duration-300 hover:scale-105 w-full sm:w-auto max-w-xs"
          >
            <span className="flex items-center justify-center gap-2">
              <span className="text-lg">‚öôÔ∏è</span>
              <span>My Skills</span>
            </span>
          </button>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-3 sm:gap-4 max-w-md sm:max-w-xl mx-auto mb-6 sm:mb-8">
          {[
            { value: '20+', label: 'Projects' },
            { value: '2+', label: 'Years Exp' },
            { value: '100%', label: 'Success' },
            { value: '24/7', label: 'Support' }
          ].map((stat, index) => (
            <div 
              key={index}
              className="bg-tech-navy/30 backdrop-blur-sm p-3 sm:p-4 rounded-lg border border-tech-cyan/10 hover:border-tech-cyan/30 transition-all duration-300"
            >
              <div className="text-xl sm:text-2xl font-bold text-tech-cyan">{stat.value}</div>
              <div className="text-xs sm:text-sm text-tech-light/60 mt-1">{stat.label}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Scroll Indicator */}
      <div className="absolute bottom-4 sm:bottom-8 left-1/2 transform -translate-x-1/2 z-20">
        <button 
          onClick={() => scrollToSection('about')}
          className="animate-bounce group cursor-pointer"
          aria-label="Scroll down"
        >
          <div className="relative">
            <div className="w-5 h-8 sm:w-6 sm:h-10 border border-tech-cyan/30 rounded-full flex justify-center group-hover:border-tech-cyan transition-colors">
              <div className="w-1 h-2 sm:h-3 bg-tech-cyan rounded-full mt-2 animate-scroll"></div>
            </div>
            <div className="absolute -bottom-5 sm:-bottom-6 left-1/2 transform -translate-x-1/2 text-tech-cyan/50 text-xs font-mono group-hover:text-tech-cyan transition-colors hidden sm:block">
              Scroll
            </div>
          </div>
        </button>
      </div>
    </section>
  );
};

export default Hero;
