import React from 'react';

const ServicesCompactV2 = () => {
  const services = [
    {
      title: "System Architecture",
      desc: "Scalable system design",
      icon: "üèóÔ∏è",
      color: "bg-blue-50 border-blue-100"
    },
    {
      title: "Web Development",
      desc: "React & Full-stack",
      icon: "üíª",
      color: "bg-purple-50 border-purple-100"
    },
    {
      title: "Mobile Apps",
      desc: "React Native",
      icon: "üì±",
      color: "bg-green-50 border-green-100"
    },
    {
      title: "API Development",
      desc: "REST & GraphQL",
      icon: "üîå",
      color: "bg-yellow-50 border-yellow-100"
    },
    {
      title: "Security",
      desc: "Auditing & Testing",
      icon: "üõ°Ô∏è",
      color: "bg-red-50 border-red-100"
    },
    {
      title: "DevOps",
      desc: "CI/CD & Cloud",
      icon: "üöÄ",
      color: "bg-cyan-50 border-cyan-100"
    },
    {
      title: "Performance",
      desc: "Optimization",
      icon: "‚ö°",
      color: "bg-orange-50 border-orange-100"
    },
    {
      title: "Consulting",
      desc: "Technical guidance",
      icon: "üí°",
      color: "bg-indigo-50 border-indigo-100"
    }
  ];

  return (
    <section id="services" className="py-8 md:py-12 bg-gray-50">
      <div className="max-w-5xl mx-auto px-4">
        {/* Minimal Header */}
        <div className="text-center mb-8">
          <h2 className="text-xl md:text-2xl font-bold text-tech-blue">
            What I Do
          </h2>
          <p className="text-gray-500 text-sm mt-2">
            Specialized services for modern development
          </p>
        </div>

        {/* Chip-style Services */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
          {services.map((service, index) => (
            <div 
              key={index}
              className={`${service.color} rounded-lg p-4 border hover:shadow-sm transition-all duration-200 group cursor-pointer`}
            >
              <div className="flex items-center gap-3 mb-2">
                <span className="text-xl">{service.icon}</span>
                <h3 className="text-sm font-semibold text-gray-800">
                  {service.title}
                </h3>
              </div>
              <p className="text-xs text-gray-600">
                {service.desc}
              </p>
              <div className="mt-3 pt-3 border-t border-gray-200/50">
                <span className="text-xs text-gray-400 group-hover:text-tech-cyan transition-colors">
                  Learn more ‚Üí
                </span>
              </div>
            </div>
          ))}
        </div>

        {/* Quick Info */}
        <div className="mt-8 flex flex-col sm:flex-row items-center justify-between gap-4 text-sm">
          <div className="text-gray-600">
            <span className="font-semibold text-tech-blue">Need a custom solution?</span>
            <span className="ml-2">Let's discuss your project.</span>
          </div>
          <button className="px-4 py-2 bg-tech-blue text-white text-sm rounded-lg hover:bg-tech-navy transition-colors">
            Get Free Quote
          </button>
        </div>
      </div>
    </section>
  );
};

export default ServicesCompactV2;
