import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:8000/api/contact/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setSubmitMessage(data.message || 'Message sent successfully!');
        setFormData({ name: '', email: '', message: '' });
      } else {
        setSubmitMessage(data.message || 'Error sending message.');
      }
    } catch (error) {
      setSubmitMessage('Network error. Try WhatsApp instead.');
    } finally {
      setIsSubmitting(false);
      setTimeout(() => setSubmitMessage(''), 5000);
    }
  };

  const whatsappLink = `https://wa.me/255750792039?text=Hi%20Kelvin,%20I%20saw%20your%20portfolio%20and%20would%20like%20to%20discuss%20a%20project.`;

  return (
    <section id="contact" className="py-10 md:py-14 bg-tech-blue text-white">
      <div className="max-w-4xl mx-auto px-4 sm:px-6">
        {/* Compact Header */}
        <div className="text-center mb-10">
          <h2 className="text-2xl md:text-3xl font-bold mb-3">
            Let's <span className="gradient-text">Connect</span>
          </h2>
          <div className="w-16 h-1 bg-gradient-to-r from-tech-cyan to-tech-green mx-auto mb-4"></div>
          <p className="text-tech-light/70 text-sm md:text-base">
            Ready to start your project? Get in touch today.
          </p>
        </div>

        {/* Contact Grid */}
        <div className="grid lg:grid-cols-3 gap-6">

          {/* Contact Info */}
          <div className="lg:col-span-1 space-y-4">
            {/* Email Card */}
            <div className="bg-tech-navy/40 backdrop-blur-sm rounded-xl p-5 border border-tech-cyan/10 hover:border-tech-cyan/30 transition-all">
              <div className="flex items-center gap-3 mb-3">
                <div className="w-10 h-10 flex items-center justify-center bg-tech-cyan/10 rounded-lg">
                  <span className="text-tech-cyan text-xl">üìß</span>
                </div>
                <div>
                  <h3 className="font-bold text-tech-light">Email</h3>
                  <p className="text-tech-light/60 text-sm">kelvincharleschacha07@gmail.com</p>
                </div>
              </div>
              <p className="text-tech-light/50 text-xs">
                Ideal for detailed project discussions
              </p>
            </div>

            {/* WhatsApp Card */}
            <a
              href={whatsappLink}
              target="_blank"
              rel="noopener noreferrer"
              className="block bg-gradient-to-r from-green-500/20 to-green-600/20 rounded-xl p-5 border border-green-400/20 hover:border-green-400/40 transition-all group"
            >
              <div className="flex items-center gap-3 mb-2">
                <div className="w-10 h-10 flex items-center justify-center bg-green-500/20 rounded-lg">
                  <span className="text-green-400 text-xl">üí¨</span>
                </div>
                <div>
                  <h3 className="font-bold text-green-300">WhatsApp</h3>
                  <p className="text-green-300/70 text-sm">+255 750 792 039</p>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <p className="text-green-300/60 text-xs">
                  Quick chat & instant replies
                </p>
                <span className="text-green-400 group-hover:translate-x-1 transition-transform">‚Üí</span>
              </div>
            </a>

            {/* Response Time */}
            <div className="bg-tech-navy/30 rounded-xl p-4 text-center">
              <div className="flex items-center justify-center gap-2 mb-2">
